using System;
using System.Collections.Generic;
using System.Linq;
using System.Web;
using System.Web.Mvc;
using lab02.Models;

namespace lab02.Controllers
{
    public class editController : Controller
    {
        // GET: edit
        public ActionResult Index()
        {
            return View();
        }
        public ActionResult edit(int id)
        {           
            var book = new List<books>();
            book.Add(new books(1, "Tuổi Trẻ Đáng Gía Bao Nhiêu?", "Rosie Nguyễn", "https://cdn0.fahasa.com/media/flashmagazine/images/page_images/tuoi_tre_dang_gia_bao_nhieu_tai_ban_2018/2019_11_05_09_19_54_1.jpg"));
            book.Add(new books(2, "Đừng chạy theo số đông", "Kiên Trần", "https://www.netabooks.vn/Data/Sites/1/Product/35663/dung-chay-theo-so-dong.jpg"));
            book.Add(new books(3, "LẬP TRÌNH VÀ CUỘC SỐNG", "Jeff  Atwood (dịch giả: Hồ Sỹ Hùng)", "data:image/jpeg;base64,"));
            books b = new books();
            foreach (books i in book)
            {
                if(i.Id == id)
                {
                    b = i;
                    break;
                }
            }
            if(b == null)
            {
                return HttpNotFound();
            }
            return View(b);
        }
        [HttpPost, ActionName("edit")]
        [ValidateAntiForgeryToken]
        public ActionResult edit(int id, string name, string author, string image)
        {
            var book = new List<books>();
            book.Add(new books(1, "Tuổi Trẻ Đáng Gía Bao Nhiêu?", "Rosie Nguyễn", "https://cdn0.fahasa.com/media/flashmagazine/images/page_images/tuoi_tre_dang_gia_bao_nhieu_tai_ban_2018/2019_11_05_09_19_54_1.jpg"));
            book.Add(new books(2, "Đừng chạy theo số đông", "Kiên Trần", "https://www.netabooks.vn/Data/Sites/1/Product/35663/dung-chay-theo-so-dong.jpg"));
            book.Add(new books(3, "LẬP TRÌNH VÀ CUỘC SỐNG", "Jeff  Atwood (dịch giả: Hồ Sỹ Hùng)", "data:image/jpeg;base64,"));
            books bo = new books();
            if(id == null)
            {
                return HttpNotFound();
            }
            foreach (books b in book)
            {
                if(b.Id == id)
                {
                    b.Name = name;
                    b.Author = author;
                    b.Image = image;
                    bo = b;
                    break;
                }
            }
            return View("booksModel", book);
        }
    }
}